def distort_ascii_art(art_lines, distortion_level):
    """ИСКАЖАЕТ ASCII АРТ ДО ПОЛНОЙ КАНАЛИЗАЦИОННОЙ СРАНИ!!!  ЕБАНЫЙ АРТ, БЛЯТЬ!!!  ХУЖЕ ТОЛЬКО В АДУ!!!"""
    distorted_art = []
    for line in art_lines:
        distorted_line = list(line)
        for i in range(len(distorted_line)):
            if random.random() < distortion_level:
                if random.random() < 0.2:
                    distorted_line[i] = random.choice(string.punctuation + string.digits + " \n\t\r\f\v" + "💩💀🤡") #  Символы, цифры, ПРОБЕЛЫ, ПРОБЕЛЬНЫЕ СИМВОЛЫ И ДАЖЕ ЭМОДЗИ ДЛЯ ТРЕША!!!  ШОБ ВООБЩЕ КУЧА ДЕРЬМА БЫЛА, СУКА!!!  ЭМОДЗИ ГОВНА И ЧЕРЕПА!!!
                elif random.random() < 0.4:
                    if distorted_line[i].isalpha():
                        distorted_line[i] = distorted_line[i].swapcase()
                elif random.random() < 0.6:
                    distorted_line[i] = " "
                elif random.random() < 0.8:
                    distorted_line[i] = random.choice(string.ascii_letters)
                else:
                    distorted_line[i] = distorted_line[i] * random.randint(3, 6) #  ДУБЛИРУЕМ ТЕПЕРЬ ОТ 3 ДО 6 РАЗ, ШОБ ВООБЩЕ НЕВОЗМОЖНО БЫЛО ГЛЯДЕТЬ НА ЭТО ГОВНО!!!  ЕЩЕ БОЛЬШЕ ДУБЛИРОВАНИЯ, СУКА!!!  ДО ШЕСТИ РАЗ!!!
        distorted_art.append("".join(distorted_line))
    return distorted_art


class ChaosTextManipulator:
    """Класс для МАНИПУЛЯЦИИ ТЕКСТОМ В АДСКОМ УГАРЕ!!!  ВЫНОС МОЗГА 7.0, БЛЯТЬ!!!  ПОСЛЕДНЯЯ ВЕРСИЯ, НАХУЙ!!!"""
    def __init__(self, text):
        self.text = text
        self.manipulation_level = GLOBAL_ПИЗДЕЦ_LEVEL #  Уровень манипуляций... ТЕПЕРЬ ЭТО УРОВЕНЬ ПОЛНОГО ПИЗДЕЦА, СУКА!!!

    def apply_random_transformations(self):
        """Применяет ДЕБИЛЬНЫЕ, ЕБАНЫЕ, АДСКИЕ ТРАНСФОРМАЦИИ к тексту!!!  ГОТОВЬТЕСЬ К ПОЛНОМУ ИНФАРКТУ И КРОВОИЗЛИЯНИЮ В МОЗГ, ПИДОРАСЫ!!!"""
        modified_text = self.text
        for _ in range(self.manipulation_level): #  Случайное количество трансформаций, ШОБ ВЫ СОВСЕМ ПОЕХАЛИ КУКУХОЙ ОТ ЭТОЙ ЕБАНОЙ ХУЙНИ!!!  ЕЩЕ БОЛЬШЕ ТРАНСФОРМАЦИЙ, МАТЬ ТВОЮ!!!
            transformation_type = random.randint(1, 12) #  12 ТИПОВ АДСКОГО БЕЗУМИЯ, БЛЯТЬ!!!  ЕЩЕ ОДИН НОВЫЙ ТИП ТРАНСФОРМАЦИИ, ЧТОБ ТЫ ОХУЕЛ, СУКА!!!
            if transformation_type == 1:
                modified_text = modified_text[::-1] #  РЕВЕРС В АДУ!!!
                GLOBAL_MESSAGE_STATE["is_reversed"] = not GLOBAL_MESSAGE_STATE["is_reversed"]
            elif transformation_type == 2:
                modified_text = modified_text.upper() #  КАПС ЛОК В АДУ!!!
                GLOBAL_MESSAGE_STATE["is_upper"] = True
            elif transformation_type == 3:
                modified_text = "".join(random.sample(list(modified_text), len(modified_text))) #  ПЕРЕМЕШИВАНИЕ БУКВ В АДУ!!!
            elif transformation_type == 4:
                if random.random() < 0.99: #  СОВСЕМ УЖЕ ЗА ГРАНЬЮ ЧАСТАЯ ЗАМЕНА БУКВ НА СИМВОЛЫ!!!  ПОЧТИ 100% ВЕРОЯТНОСТЬ, МАТЬ ТВОЮ!!!  ПОЧТИ ВСЕГДА-ВСЕГДА-ВСЕГДА!!!
                    modified_text = modified_text.replace('o', '0').replace('e', '3').replace('l', '1').replace('H', '#').replace('W', '@').replace('A', '^').replace('N', '~').replace('M', '%').replace('S', '$').replace('I', '!').replace('T', '7').replace('B', '8').replace('G', '6').replace('P', '*').replace('R', '&').replace('K', '+').replace('X', '><').replace('Y', 'ж').replace('V', 'v') #  АДСКИЙ 1337, БЛЯТЬ!!!  ЕЩЕ БОЛЬШЕ СИМВОЛОВ!!!  ЗАМЕНЕНЫ УЖЕ ВСЕ БУКВЫ, КРОМЕ ГЛАСНЫХ, СУКА!!!  ЧТОБ ТЫ СОВСЕМ НИЧЕГО НЕ ПОНЯЛ!!!
            elif transformation_type == 5:
                if random.random() < 0.95: #  СОВСЕМ УЖЕ ЗА ГРАНЬЮ ЧАСТОЕ УДАЛЕНИЕ СЛУЧАЙНЫХ БУКВ!!!  ПОЧТИ 100% ВЕРОЯТНОСТЬ, МАТЬ ТВОЮ!!!  УДАЛЯЕМ ПОЧТИ ВСЕ БУКВЫ, СУКА!!!  ЧТОБ ТАМ ОДНИ СИМВОЛЫ ОСТАЛИСЬ!!!
                    modified_text_list = list(modified_text)
                    for i in random.sample(range(len(modified_text_list)), random.randint(0, len(modified_text_list) // 2)):
                        modified_text_list[i] = ''
                    modified_text = "".join(modified_text_list)
            elif transformation_type == 6: #  СДВИГ СИМВОЛОВ!!!  АДСКИЙ СДВИГ, БЛЯТЬ!!!  СДВИГАЕМ В АД!!!
                shift = random.randint(-25, 25) #  Сдвигаем на ЕЩЕ БОЛЕЕ СЛУЧАЙНОЕ число позиций!!!  ДИАПАЗОН СДВИГА УЖЕ ПРОСТО БЕЗУМНЫЙ!!!  КАК В АДУ!!!
                shifted_text = ""
                for char in modified_text:
                    try: #  ОПЯТЬ ОБРАБОТКА ОШИБОК, ШОБ ЭТА ХУЙНЯ НЕ ВЫЛЕТАЛА, А ПРОСТО БЕСИЛА ТЕБЯ, СУКА!!!
                        shifted_char = chr(ord(char) + shift)
                    except ValueError:
                        shifted_char = char
                    shifted_text += shifted_char
                modified_text = shifted_text
            elif transformation_type == 7: #  ПОВТОРЕНИЕ СИМВОЛОВ!!!  АДСКОЕ ЭХО, БЛЯТЬ!!!  ЭХО ИЗ ПРЕИСПОДНЕЙ!!!
                repeated_text = ""
                for char in modified_text:
                    repeated_text += char * random.randint(1, 12) #  Повторяем до 12 раз, ШОБ ВООБЩЕ НЕВОЗМОЖНО БЫЛО РАЗГЛЯДЕТЬ ХОТЬ ЧТО-ТО, СУКА!!!  ЕЩЕ БОЛЬШЕ ПОВТОРЕНИЙ!!!  ДО ДВЕНАДЦАТИ РАЗ!!!  КАК КРИКИ ГРЕШНИКОВ В АДУ!!!
                modified_text = repeated_text
            elif transformation_type == 8: #  ЗАМЕНА СЛУЧАЙНЫМИ СИМВОЛАМИ!!!  ПОЛНЫЙ АД, БЛЯТЬ!!!  АДЪ АДСКИЙ!!!
                absurd_chars = string.printable
                modified_text_list = list(modified_text)
                for i in range(len(modified_text_list)):
                    if random.random() < 0.7: #  ЗАМЕНЯЕМ УЖЕ ПОЧТИ ВСЕ БУКВЫ!!!  ПОЧТИ ВСЕ 70%, СУКА!!!  ПОЧТИ ВЕСЬ ТЕКСТ - СЛУЧАЙНАЯ ХУЙНЯ!!!  ЧТОБЫ ТЫ СОВСЕМ ОХУЕЛ!!!
                        modified_text_list[i] = random.choice(absurd_chars)
                    modified_text = "".join(modified_text_list)
            elif transformation_type == 9: #  ЦИКЛИЧЕСКИЙ СДВИГ ТЕКСТА!!!  НОВЫЙ ТИП АДА!!!  ЦИКЛИЧЕСКИЙ АД!!!
                rotation = random.randint(1, len(modified_text) - 1) if modified_text else 0
                modified_text = modified_text[rotation:] + modified_text[:rotation]
                GLOBAL_MESSAGE_STATE["is_rotated"] = not GLOBAL_MESSAGE_STATE["is_rotated"]
            elif transformation_type == 10: #  ОТРАЖЕНИЕ ТЕКСТА!!!  ВОТ ЭТО ПОДЛЫЙ АД, БЛЯТЬ!!!  ЗЕРКАЛЬНЫЙ АД!!!
                modified_text = "".join([char[::-1] for char in modified_text.split(" ")])
